import React from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { BrowserRouter, Link, Route } from 'react-router-dom';
import { signout } from './actions/userActions';
import AdminRoute from './components/AdminRoute';
import PrivateRoute from './components/PrivateRoute';
import CartScreen from './screens/CartScreen';
import HomeScreen from './screens/HomeScreen';
import OrderHistoryScreen from './screens/OrderHistoryScreen';
import OrderScreen from './screens/OrderScreen';
import PaymentMethodScreen from './screens/PaymentMethodScreen';
import PlaceOrderScreen from './screens/PlaceOrderScreen';
import ProductListScreen from './screens/ProductListScreen';
import ProductScreen from './screens/ProductScreen';
import ProfileScreen from './screens/ProfileScreen';
import RegisterScreen from './screens/RegisterScreen';
import ShippingAddressScreen from './screens/ShippingAddressScreen';
import SigninScreen from './screens/SigninScreen';
import ProductEditScreen from './screens/ProductEditScreen';
import OrderListScreen from './screens/OrderListScreen';
import AboutScreen from './screens/AboutScreen'

function App() {
  const cart = useSelector((state) => state.cart);
  const { cartItems } = cart;
  const userSignin = useSelector((state) => state.userSignin);
  const { userInfo } = userSignin;
  const userName = '';
  if (userInfo) {
     userName = userInfo.name.split(" ")[0];
  }
  const dispatch = useDispatch();
  const signoutHandler = () => {
    dispatch(signout());
  };
  return (
    <BrowserRouter>
      <div className="grid-container">
        <header className="row_nav">
        {userInfo ? (     
          <div> 
              <div className="dropdown">
                <Link className="padding_left" to="#">
                <i className="fa fa-caret-down"></i>{' '} {userName} 
                </Link>
                <ul className="dropdown-content">
                  <li className="left-text">
                    <Link to="/profile">User Profile</Link>
                  </li>
                  <li className="left-text">
                    <Link to="/orderhistory">Order History</Link>
                  </li>
                  <li className="left-text">
                    <Link to="#signout" onClick={signoutHandler}>
                      Sign Out
                    </Link>
                  </li>
                </ul>
              </div>
              </div>  
            ) : (
              <div>
              <Link className="padding_left" to="/signin">Sign In</Link>
              </div>
            )}
          <div>
            <Link className="brand" to="/">
              <img className="big_small" src="https://unsad-bucket.s3.us-east-2.amazonaws.com/tentative_logo.jpg" alt="logo"></img>
            </Link>
          </div>
          <div className="right_align">
          <Link className="padding" to="/about">
              About
            </Link>
            <Link className="padding" to="/cart">
              Cart
              {cartItems.length > 0 && (
                <span className="badge">{cartItems.length}</span>
              )}
            </Link >

            {userInfo && userInfo.isAdmin && (
              <div className="dropdown">
                <Link className="padding" to="#admin">
                  Admin <i className="fa fa-caret-down"></i>
                </Link>
                <ul className="dropdown-content">
                  <li>
                    <Link to="/dashboard">Dashboard</Link>
                  </li>
                  <li>
                    <Link to="/productlist">Products</Link>
                  </li>
                  <li>
                    <Link to="/orderlist">Orders</Link>
                  </li>
                  <li>
                    <Link to="/userlist">Users</Link>
                  </li>
                </ul>
              </div>
            )}
          </div>
        </header>
        <main>
          <Route path="/cart/:id?" component={CartScreen}></Route>
          <Route path="/product/:id/:size/:completelyOutOfStock" component={ProductScreen} exact></Route>
          <Route
            path="/product/:id/edit"
            component={ProductEditScreen}
            exact
          ></Route>
          <Route path="/about" component={AboutScreen}></Route>
          <Route path="/signin" component={SigninScreen}></Route>
          <Route path="/register" component={RegisterScreen}></Route>
          <Route path="/shipping" component={ShippingAddressScreen}></Route>
          <Route path="/payment" component={PaymentMethodScreen}></Route>
          <Route path="/placeorder" component={PlaceOrderScreen}></Route>
          <Route path="/order/:id" component={OrderScreen}></Route>
          <Route path="/orderhistory" component={OrderHistoryScreen}></Route>
          <PrivateRoute
            path="/profile"
            component={ProfileScreen}
          ></PrivateRoute>
          <AdminRoute
            path="/productlist"
            component={ProductListScreen}
          ></AdminRoute>
          <AdminRoute
            path="/orderlist"
            component={OrderListScreen}
          ></AdminRoute>
          <Route path="/" component={HomeScreen} exact></Route>
        </main>
        <footer className="foot_stack">
        <div class="social-media">
                    <ul class="nav-list">
                        <li>
                            <a href="https://www.instagram.com/unsad.co/" class="icon-link">
                                <i class="fab fa-instagram" aria-hidden="true"></i>
                            </a>
                        </li>
                        <li>
                            <a href="mailto:alest314@gmail.com" class="icon-link">
                                <i class="fas fa-envelope"></i>
                            </a>
                        </li>
                        <li>
                            <a href="tel:1-631-935-2590" class="icon-link">
                                <i class="fas fa-phone"></i>
                            </a>
                        </li> 
                    </ul>
                </div>
          <br></br>
          <div>@2020 all rights reserved</div>

          </footer>

      </div>
    </BrowserRouter>
  );
}
export default App;
